{
  "name": "aglio",
  "version": "2.3.0",
  "description": "An API Blueprint renderer with theme support",
  "main": "lib/main.js",
  "bin": {
    "aglio": "./bin/aglio.js"
  },
  "scripts": {
    "test": "grunt test",
    "prepublish": "grunt compile",
    "blanket": {
      "pattern": [
        "aglio/lib"
      ],
      "data-cover-never": "node_modules"
    }
  },
  "author": "Daniel G. Taylor",
  "license": "MIT",
  "repository": {
    "type": "git",
    "url": "https://github.com/danielgtaylor/aglio"
  },
  "bugs": {
    "url": "https://github.com/danielgtaylor/aglio/issues"
  },
  "keywords": [
    "api",
    "blueprint",
    "protagonist",
    "snowcrash",
    "html",
    "parse",
    "markdown"
  ],
  "dependencies": {
    "aglio-theme-olio": "^1.6.3",
    "chokidar": "^1.4.1",
    "cli-color": "^1.1.0",
    "pretty-error": "^1.2.0",
    "drafter": "^1.0.0",
    "serve-static": "^1.10.0",
    "socket.io": "^1.3.7",
    "yargs": "^3.31.0"
  },
  "devDependencies": {
    "async": "^1.5.0",
    "coffeelint": "^1.14.2",
    "grunt": "~0.4.5",
    "grunt-coffeelint": "0.0.13",
    "grunt-contrib-coffee": "^0.13.0",
    "grunt-mocha-cov": "^0.4.0",
    "sinon": "^1.17.2"
  }
}
