files.Cargo.toml:
[workspace]
resolver = "2"
members = [
	"base",
	"pallets/dactr",
	"pallets/mandate",
	"pallets/system",
	"pallets/vector",
	"patricia-merkle-trie",
	"client/basic-authorship",
	"runtime",
	"runtime/fuzz",
	"node",
]

[workspace.package]
authors = ["Avail Project <info@availproject.org>"]
edition = "2021"
repository = "https://github.com/availproject/avail"
license = "GNU GPLv3"
homepage = "https://www.availproject.org/"


[workspace.dependencies]
avail-core = { git = "https://github.com/availproject/avail-core.git", tag = "core-node-12", default-features = false, features = [ "runtime"] }
kate = { git = "https://github.com/availproject/avail-core", tag = "core-node-12", default-features = false }
kate-recovery = { git = "https://github.com/availproject/avail-core", tag = "core-node-12", default-features = false  }
#avail-core = { path = "../avail-core/core", default-features = false, features = [ "runtime"] }
#kate = { path = "../avail-core/kate/", default-features = false }
#kate-recovery = { path = "../avail-core/kate/recovery/", default-features = false}

avail-base = { path = "base", default-features = false }
da-control = { path = "pallets/dactr", default-features = false }
pallet-mandate = { path = "pallets/mandate", default-features = false }
pallet-vector = { path = "pallets/vector", default-features = false }
da-runtime = { path = "runtime", default-features = false }
kate-rpc = { path = "rpc/kate-rpc" }
testing-rpc = { path = "rpc/testing-rpc" }
patricia-merkle-trie = { path = "patricia-merkle-trie", default-features = false }

sc-basic-authorship = { path = "client/basic-authorship", default-features = false }
frame-system = { path = "pallets/system", default-features = false }
frame-system-rpc-runtime-api = { path = "pallets/system/rpc/runtime-api", default-features = false }
frame-system-benchmarking = { path = "pallets/system/benchmarking", default-features = false }


# benchmarking
criterion = { version = "0.4", default-features = false }
iai = "0.1.1"
iai-callgrind = "0.7.3"
divan = "0.1.11"

# Logging and testing
log = "0.4.20"
test-case = "1.2.3"
hex = "0.4"
const-hex = { version = "1.14.1", default-features = false, features = ["alloc"] }
env_logger = "0.9.1"
thiserror-no-std = "2.0.2"

# Futures, async and multi-threading
futures = "0.3.21"
futures-timer = "3.0.1"
parking_lot = "0.12.1"
once_cell = "1.17.1"
async-trait = "0.1.74"
rayon = "1.5.2"

# Macros and code generation
hex-literal = "0.3.4"
static_assertions = "1.1.0"
serde = { version = "1.0.197", default-features = false, features = ["derive"] }
serde_json = { version = "1.0", default-features = false }
derive_more = { version = "0.99.17", default-features = false, features = ["from", "into", "display"] }
scale-info = { version = "2.5.0", default-features = false, features = ["derive","serde"] }
cfg-if = "1.0"
impl-trait-for-tuples = "0.2.1"
docify = "0.2.6"

# Encryptions and hashing
sha2 = { version = "0.10.8", default-features = false }
hash256-std-hasher = { vers
... (truncated)

[workspace]
members = [
	"bootstrap",
	"client",
	"compatibility-tests",
	"core",
	"crawler",
	"fat",
	"monitor-client",
	"p2p-load-test",
	"web",
]
default-members = ["client"]
resolver = "2"

[workspace.package]
authors = ["Avail Team"]
repository = "https://github.com/availproject/avail-light"

[workspace.dependencies]
# Internal deps
avail-core = { git = "https://github.com/availproject/avail-core", default-features = false, tag = "core-node-11", features = ["serde"] }
avail-rust = { git = "https://github.com/availproject/avail-rust.git", rev = "e2e02a7b6d08fb7a8cffad8c2856b742b6587b1f", default-features = false }
kate = { git = "https://github.com/availproject/avail-core", default-features = false, tag = "core-node-11", features = ["serde"] }
kate-recovery = { git = "https://github.com/availproject/avail-core", default-features = false, tag = "core-node-11", features = ["serde"] }
avail-light-core = { path = "./core", default-features = false }
sp-core = { git = "https://github.com/availproject/polkadot-sdk.git", tag = "polkadot-1.7.1-patch-12", default-features = false, features = ["serde"] }

anyhow = "1.0.71"
async-std = { version = "1.12.0", features = ["attributes"] }
async-trait = "0.1.73"
chrono = "0.4"
clap = { version = "4.4.4", features = ["derive", "cargo"] }
color-eyre = { version = "0.6.2", default-features = false }
confy = "0.5.1"
hex = "0.4.3"
rand = { version = "0.8.5", default-features = false }
reqwest = { version = "0.12" }
libp2p = { version = "0.55.0", features = ["kad", "identify", "ping", "autonat", "relay", "noise", "yamux", "dns", "metrics", "tokio", "macros", "tcp", "quic", "serde", "websocket", "plaintext"] }
libp2p-allow-block-list = "0.5.0"
libp2p-webrtc-websys = "0.4.0"
multihash = { version = "0.14.0", default-features = false, features = ["blake3", "sha3"] }
semver = "1.0.23"
serde = { version = "1.0", features = ["derive"] }
tokio = { version = "1.35", default-features = false, features = ["sync", "macros", "io-util", "rt", "time"] }
tokio-stream = { version = "0.1.15", features = ["sync"] }
tracing = "0.1.35"
tracing-log = "0.2.0"
tracing-subscriber = { version = "0.3.17", features = ["json", "env-filter"] }
uuid = { version = "1.3.4", features = ["v4", "fast-rng", "macro-diagnostics", "serde"] }
void = "1.0.2"
warp = "0.3.6"
futures = { version = "0.3.15", default-features = false, features = ["std", "async-await"] }

# OpenTelemetry
opentelemetry = "0.27.1"
opentelemetry-otlp = { version = "0.27.0", features = ["grpc-tonic", "metrics"] }
opentelemetry_sdk = { version = "0.27.1", features = ["metrics", "rt-tokio"] }

[profile.debug-fast]
inherits = "release"
debug = true

[profile.release]
incremental = false

[profile.maxperf]
inherits = "release"
lto = "fat"
codegen-units = 1

